    cursor = conn.cursor()
    for i,surat in enumerate(surats):
        seq_start = seq[i]
        seq_end = seq[i+1]
        sql = 'insert into quran_surat_tab (surat_id,description) values (%s,%s)'
        args = i+1,surat
        cursor.execute(sql,args)
        for ayat_idx, ayat in enumerate(ayats[seq_start:seq_end]):
            sql = 'insert into quran_ayat_tab (surat_id,ayat_id,description) values (%s,%s,%s)'
            args = i+1,ayat_idx+1,ayat
            cursor.execute(sql,args)
